mapper.select:
SELECT CASE WHEN EXISTS (
        SELECT USER_ID
        FROM system.F8_USER
        WHERE USER_ID = #{userId}
) THEN 1 ELSE 0 END
FROM dual

mapper.update:
UPDATE system.F8_USER
SET USER_NAME = #{userName}
  , AGE = #{age}
  , GMAIL = #{gmail}
  , ROLE_ID = #{roleId}
   ,DESCRIPTION = #{description}
WHERE USER_ID = #{userId}
